import React, { useState } from 'react';
import { 
  Container, 
  Grid, 
  Card, 
  CardContent, 
  Typography, 
  Button,
  Box
} from '@mui/material';

interface CardSymbol {
  value: number;
  timestamp: number;
}

const GamePage: React.FC = () => {
  const [sequence, setSequence] = useState<CardSymbol[]>([]);
  const symbols = ['♠', '♥', '♦', '♣'];

  const addSymbol = (value: number) => {
    const newSymbol: CardSymbol = {
      value,
      timestamp: Date.now()
    };
    setSequence(prev => [...prev, newSymbol]);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Card Game
        </Typography>

        <Grid container spacing={2} sx={{ mb: 4 }}>
          {symbols.map((symbol, index) => (
            <Grid item xs={3} key={index}>
              <Button 
                variant="contained" 
                fullWidth 
                onClick={() => addSymbol(index)}
                sx={{ fontSize: '2rem', height: '80px' }}
              >
                {symbol}
              </Button>
            </Grid>
          ))}
        </Grid>

        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Sequence History
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              flexWrap: 'wrap', 
              gap: 1,
              maxHeight: '200px',
              overflowY: 'auto'
            }}>
              {sequence.map((item, index) => (
                <Typography 
                  key={index} 
                  variant="h5" 
                  component="span"
                  sx={{ opacity: 1 - (sequence.length - 1 - index) * 0.1 }}
                >
                  {symbols[item.value]}
                </Typography>
              ))}
            </Box>
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};

export default GamePage;
