import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Card, 
  CardContent, 
  Typography, 
  Button,
  Box,
  ButtonGroup
} from '@mui/material';

interface CardSymbol {
  symbol: number;
  timestamp: string;
}

const GamePage: React.FC = () => {
  const [sequence, setSequence] = useState<CardSymbol[]>([]);
  const symbols = ['♠', '♥', '♦', '♣'];

  const loadSequence = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/sequences/1');
      const data = await response.json();
      setSequence(data);
    } catch (error) {
      console.error('Error loading sequence:', error);
    }
  };

  // Load sequence when component mounts
  useEffect(() => {
    loadSequence();
  }, []);

  const addSymbol = async (value: number) => {
    try {
      await fetch('http://localhost:5000/api/sequences/1/symbol', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ value }),
      });

      // Reload sequence from database
      loadSequence();
    } catch (error) {
      console.error('Error adding symbol:', error);
    }
  };

  const simulateSequence = async () => {
    for (let i = 0; i < 90; i++) {
      const randomValue = Math.floor(Math.random() * 4);
      await addSymbol(randomValue);
      // Small delay to prevent overwhelming the server
      await new Promise(resolve => setTimeout(resolve, 10));
    }
  };

  const clearDatabase = async () => {
    try {
      await fetch('http://localhost:5000/api/sequences/cleanup', {
        method: 'DELETE',
      });
      setSequence([]);
    } catch (error) {
      console.error('Error clearing database:', error);
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Card Game
        </Typography>

        <Grid container spacing={2} sx={{ mb: 4 }}>
          {symbols.map((symbol, index) => (
            <Grid item xs={3} key={index}>
              <Button 
                variant="contained" 
                fullWidth 
                onClick={() => addSymbol(index)}
                sx={{ fontSize: '2rem', height: '80px' }}
              >
                {symbol}
              </Button>
            </Grid>
          ))}
        </Grid>

        <Box sx={{ mb: 4 }}>
          <ButtonGroup variant="contained" color="secondary" fullWidth>
            <Button onClick={simulateSequence}>
              Generate 90 Random
            </Button>
            <Button onClick={clearDatabase}>
              Clear All
            </Button>
          </ButtonGroup>
        </Box>

        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Current Sequence
            </Typography>
            <Box sx={{ 
              display: 'flex', 
              flexWrap: 'wrap', 
              gap: 1, 
              maxHeight: '200px', 
              overflowY: 'auto' 
            }}>
              {sequence.map((item, index) => (
                <Typography key={index} sx={{ fontSize: '1.5rem' }}>
                  {symbols[item.symbol]}
                </Typography>
              ))}
            </Box>
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};

export default GamePage;
