import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Card, 
  CardContent, 
  Typography, 
  Button,
  Box,
  ButtonGroup,
  IconButton,
  Tooltip,
  LinearProgress,
  Alert,
  Paper
} from '@mui/material';
import UndoIcon from '@mui/icons-material/Undo';

interface CardSymbol {
  symbol: number;
  timestamp: string;
}

interface ThresholdInfo {
  level: number;
  message: string;
  color: string;
}

const GamePage: React.FC = () => {
  const [sequence, setSequence] = useState<CardSymbol[]>([]);
  const [thresholdInfo, setThresholdInfo] = useState<ThresholdInfo>({ 
    level: 1, 
    message: '100 more symbols until Markov Chain Analysis', 
    color: 'warning' 
  });
  const symbols = ['♠', '♥', '♦', '♣'];

  const loadSequence = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/sequences/1');
      const data = await response.json();
      // Ensure we're getting an array of symbols
      if (Array.isArray(data.symbols)) {
        const formattedSequence = data.symbols.map((symbol: number): CardSymbol => ({ symbol }));
        setSequence(formattedSequence);
        setThresholdInfo(getThresholdInfo(formattedSequence.length));
      } else {
        console.error('Expected array of symbols but got:', data);
        setSequence([]);
      }
    } catch (error) {
      console.error('Error loading sequence:', error);
      setSequence([]);
    }
  };

  useEffect(() => {
    loadSequence();
  }, []);

  const addSymbol = async (symbolIndex: number) => {
    try {
      await fetch('http://localhost:5000/api/sequences/1/symbol', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ symbol: symbolIndex }),
      });

      const newSymbol: CardSymbol = { symbol: symbolIndex };
      setSequence(prevSequence => [...prevSequence, newSymbol]);
      setThresholdInfo(prevInfo => getThresholdInfo(sequence.length + 1));
    } catch (error) {
      console.error('Error adding symbol:', error);
    }
  };

  const undoLastSymbol = async () => {
    if (sequence.length === 0) return;

    try {
      await fetch('http://localhost:5000/api/sequences/1/undo', {
        method: 'DELETE',
      });
      setSequence(prevSequence => prevSequence.slice(0, -1));
      setThresholdInfo(prevInfo => getThresholdInfo(sequence.length - 1));
    } catch (error) {
      console.error('Error undoing last symbol:', error);
    }
  };

  const simulateSequence = async () => {
    for (let i = 0; i < 90; i++) {
      const randomValue = Math.floor(Math.random() * 4);
      await addSymbol(randomValue);
      // Small delay to prevent overwhelming the server
      await new Promise(resolve => setTimeout(resolve, 10));
    }
  };

  const clearDatabase = async () => {
    try {
      await fetch('http://localhost:5000/api/sequences/cleanup', {
        method: 'DELETE',
      });
      setSequence([]);
      setThresholdInfo({ 
        level: 1, 
        message: '100 more symbols until Markov Chain Analysis', 
        color: 'warning' 
      });
    } catch (error) {
      console.error('Error clearing database:', error);
    }
  };

  const getThresholdInfo = (count: number): ThresholdInfo => {
    if (count >= 300) {
      return {
        level: 4,
        message: 'Autocorrelation Analysis Active',
        color: 'success'
      };
    } else if (count >= 200) {
      return {
        level: 3,
        message: 'Runs Test Analysis Active',
        color: 'info'
      };
    } else if (count >= 100) {
      return {
        level: 2,
        message: 'Markov Chain Analysis Active',
        color: 'primary'
      };
    } else {
      return {
        level: 1,
        message: `${100 - count} more symbols until Markov Chain Analysis`,
        color: 'warning'
      };
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          RNG Analysis Game
        </Typography>

        <Grid container spacing={2} sx={{ mb: 4 }}>
          {symbols.map((symbol, index) => (
            <Grid item xs={3} key={index}>
              <Button 
                variant="contained" 
                fullWidth 
                onClick={() => addSymbol(index)}
                sx={{ fontSize: '2rem', height: '80px' }}
              >
                {symbol}
              </Button>
            </Grid>
          ))}
        </Grid>

        <Box sx={{ mb: 4 }}>
          <ButtonGroup variant="contained" color="secondary" fullWidth>
            <Button onClick={simulateSequence}>
              Generate 90 Random
            </Button>
            <Button onClick={clearDatabase}>
              Clear All
            </Button>
            <Tooltip title="Undo last symbol">
              <IconButton 
                onClick={undoLastSymbol}
                disabled={sequence.length === 0}
                sx={{ ml: 1 }}
              >
                <UndoIcon />
              </IconButton>
            </Tooltip>
          </ButtonGroup>
        </Box>

        <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">
              Current Sequence ({sequence.length} symbols)
            </Typography>
          </Box>

          <LinearProgress 
            variant="determinate" 
            value={Math.min((sequence.length / 300) * 100, 100)} 
            color={thresholdInfo.color as any}
            sx={{ mb: 2 }}
          />

          <Alert severity={thresholdInfo.color as any} sx={{ mb: 2 }}>
            {thresholdInfo.message}
          </Alert>

          <Box sx={{ 
            display: 'flex', 
            flexWrap: 'wrap', 
            gap: 1, 
            maxHeight: '200px', 
            overflowY: 'auto', 
            mb: 3,
            p: 2,
            border: '1px solid #eee',
            borderRadius: 1
          }}>
            {sequence.map((item, index) => (
              <Typography key={index} variant="body1">
                {symbols[item.symbol]}
              </Typography>
            ))}
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default GamePage;
