import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Paper,
  CircularProgress,
  Alert,
  Grid,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Tooltip,
  ButtonGroup,
  Divider
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip as ChartTooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  ChartTooltip,
  Legend
);

interface AnalysisData {
  markovChain?: {
    matrix: Record<string, Record<string, number>>;
    prediction: number | null;
    confidence: number;
    accuracy: number;
  };
  entropy?: {
    entropy: number;
    prediction: number | null;
    confidence: number;
    accuracy: number;
  };
  chiSquare?: {
    chiSquare: number;
    pValue: number;
    prediction: number | null;
    confidence: number;
    accuracy: number;
  };
  monteCarlo?: {
    simulations: number;
    prediction: number | null;
    confidence: number;
    accuracy: number;
  };
}

const AnalysisPage: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [sequences, setSequences] = useState<number[]>([]);
  const [analysisData, setAnalysisData] = useState<AnalysisData>({});
  const [generatingData, setGeneratingData] = useState(false);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('http://localhost:5000/api/analysis/1');
      const data = await response.json();

      console.log('Analysis page received:', data); // Debug log

      if (!data || !Array.isArray(data.symbols)) {
        console.error('Invalid sequence data received:', data);
        setError('Invalid data format received from server');
        setSequences([]);
        return;
      }

      setSequences(data.symbols);
      if (data.analyses) {
        console.log('Setting analysis data:', data.analyses); // Debug log
        setAnalysisData(data.analyses);
      } else {
        console.warn('No analyses data in response');
        setAnalysisData({});
      }
    } catch (err) {
      console.error('Error fetching analysis data:', err);
      setError('Failed to fetch analysis data');
    } finally {
      setLoading(false);
    }
  };

  const generateTestData = async () => {
    try {
      setGeneratingData(true);
      setError(null);

      const response = await fetch('http://localhost:5000/api/sequences/generate-test-data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ count: 90 })
      });

      if (!response.ok) {
        throw new Error('Failed to generate test data');
      }

      const result = await response.json();
      console.log('Generated test data:', result);

      await fetchData(); // Refresh the analysis after generating data
    } catch (err) {
      console.error('Error generating test data:', err);
      setError('Failed to generate test data');
    } finally {
      setGeneratingData(false);
    }
  };

  const clearData = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('http://localhost:5000/api/sequences/cleanup', {
        method: 'POST'
      });

      if (!response.ok) {
        throw new Error('Failed to clear data');
      }

      await fetchData(); // Refresh after clearing
    } catch (err) {
      console.error('Error clearing data:', err);
      setError('Failed to clear data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const renderTransitionMatrix = () => {
    if (!analysisData.markovChain?.matrix) return null;

    const matrix = analysisData.markovChain.matrix;
    const symbols = Array.from(new Set([
      ...Object.keys(matrix),
      ...Object.values(matrix).flatMap(row => Object.keys(row))
    ])).sort();

    return (
      <TableContainer component={Paper} sx={{ mt: 2 }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>From ↓ To →</TableCell>
              {symbols.map(symbol => (
                <TableCell key={symbol}>{symbol}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {symbols.map(fromSymbol => (
              <TableRow key={fromSymbol}>
                <TableCell component="th" scope="row">{fromSymbol}</TableCell>
                {symbols.map(toSymbol => (
                  <TableCell key={toSymbol}>
                    {(matrix[fromSymbol]?.[toSymbol] || 0).toFixed(2)}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  };

  const renderPredictionCard = (
    title: string,
    prediction: number | null,
    confidence: number,
    accuracy: number,
    extraInfo?: React.ReactNode
  ) => (
    <Paper elevation={3} sx={{ p: 3, height: '100%' }}>
      <Typography variant="h6" gutterBottom>
        {title}
      </Typography>
      <Box sx={{ mb: 2 }}>
        <Typography variant="body1" color="text.secondary">
          Next Symbol Prediction
        </Typography>
        <Typography variant="h4">
          {prediction !== null ? prediction : 'N/A'}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Confidence: {(confidence * 100).toFixed(1)}%
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Historical Accuracy: {(accuracy * 100).toFixed(1)}%
        </Typography>
      </Box>
      {extraInfo}
    </Paper>
  );

  const renderChart = () => {
    if (!Array.isArray(sequences) || sequences.length === 0) {
      return <Alert severity="info">No sequence data available yet.</Alert>;
    }

    const chartData = {
      labels: sequences.map((_, index) => index + 1),
      datasets: [
        {
          label: 'Symbol Values',
          data: sequences,
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }
      ]
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'top' as const,
        },
        title: {
          display: true,
          text: 'Sequence Pattern'
        }
      }
    };

    return <Line data={chartData} options={options} />;
  };

  if (loading && !generatingData) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container>
      <Box sx={{ mt: 4, mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4">
          Analysis Dashboard
        </Typography>
        <ButtonGroup variant="contained">
          <Button
            startIcon={<AddIcon />}
            onClick={generateTestData}
            disabled={loading || generatingData}
          >
            Generate Test Data
          </Button>
          <Button
            startIcon={<DeleteIcon />}
            onClick={clearData}
            disabled={loading || generatingData}
            color="error"
          >
            Clear Data
          </Button>
          <Button
            startIcon={<RefreshIcon />}
            onClick={fetchData}
            disabled={loading || generatingData}
          >
            Refresh
          </Button>
        </ButtonGroup>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Sequence Overview
            </Typography>
            {sequences.length === 0 ? (
              <Alert severity="info">
                No sequences recorded yet. Use the "Generate Test Data" button above to create test data, or add symbols manually in the Game page.
              </Alert>
            ) : (
              <Typography>
                Total Symbols: {sequences.length}
              </Typography>
            )}
          </Paper>
        </Grid>

        <Grid item xs={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Pattern Visualization
            </Typography>
            {renderChart()}
          </Paper>
        </Grid>

        {analysisData.markovChain && (
          <Grid item xs={12}>
            {renderPredictionCard(
              'Markov Chain Analysis',
              analysisData.markovChain.prediction,
              analysisData.markovChain.confidence,
              analysisData.markovChain.accuracy,
              <>
                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                  Transition Matrix
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  This matrix shows the probability of transitioning from one symbol to another.
                  Each cell represents P(column|row) - the probability of getting the column's symbol after seeing the row's symbol.
                </Typography>
                {renderTransitionMatrix()}
              </>
            )}
          </Grid>
        )}

        <Grid item xs={12} md={6}>
          {analysisData.entropy && renderPredictionCard(
            'Entropy Analysis',
            analysisData.entropy.prediction,
            analysisData.entropy.confidence,
            analysisData.entropy.accuracy,
            <>
              <Divider sx={{ my: 2 }} />
              <Typography variant="body2" color="text.secondary">
                Entropy Score: {analysisData.entropy.entropy.toFixed(3)}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Lower entropy indicates more predictable patterns, while higher entropy suggests more randomness.
              </Typography>
            </>
          )}
        </Grid>

        <Grid item xs={12} md={6}>
          {analysisData.chiSquare && renderPredictionCard(
            'Chi-Square Test',
            analysisData.chiSquare.prediction,
            analysisData.chiSquare.confidence,
            analysisData.chiSquare.accuracy,
            <>
              <Divider sx={{ my: 2 }} />
              <Typography variant="body2" color="text.secondary">
                χ² Value: {analysisData.chiSquare.chiSquare.toFixed(3)}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                p-value: {analysisData.chiSquare.pValue.toFixed(3)}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                A low p-value ({"<"} 0.05) suggests the sequence is not random.
              </Typography>
            </>
          )}
        </Grid>

        <Grid item xs={12} md={6}>
          {analysisData.monteCarlo && renderPredictionCard(
            'Monte Carlo Simulation',
            analysisData.monteCarlo.prediction,
            analysisData.monteCarlo.confidence,
            analysisData.monteCarlo.accuracy,
            <>
              <Divider sx={{ my: 2 }} />
              <Typography variant="body2" color="text.secondary">
                Based on {analysisData.monteCarlo.simulations.toLocaleString()} simulations
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Predictions are made by running multiple simulations using the observed probability distribution.
              </Typography>
            </>
          )}
        </Grid>
      </Grid>
    </Container>
  );
};

export default AnalysisPage;
